mapper.update:
UPDATE routines
SET part_1 = #{part1}, part_2 = #{part2}, title = #{title}, content = #{content}, workout_time = #{workoutTime}
WHERE routine_id = #{id}

UPDATE routines
SET delete_at = now()
WHERE routine_id = #{id}

mapper.select:
SELECT * FROM routines
WHERE ${key} LIKE CONCAT('%', #{word}, '%')
ORDER BY ${orderBy} ${orderByDir}
